mapper.update:

update favorite
set userId = #{userid,jdbcType=INTEGER},
  movieId = #{movieid,jdbcType=INTEGER},
  tvplayId = #{tvplayid,jdbcType=INTEGER},
  status = #{status,jdbcType=INTEGER}
where favoriteId = #{favoriteid,jdbcType=INTEGER}

mapper.select:

select favoriteId, userId, movieId, tvplayId, status
from favorite
where favoriteId = #{favoriteid,jdbcType=INTEGER}

select favoriteId, userId, movieId, tvplayId, status
from favorite
where userId = #{userid}

select favoriteId, userId, movieId, tvplayId, status
from favorite
where movieId = #{movieid} and userId=#{userid}

select favoriteId, userId, movieId, tvplayId, status
from favorite
where tvplayId = #{tvplayid} and userId=#{userid}
